// ==UserScript==
// @name BitSnoop
// @trackerURL https://bitsnoop.com
// @icon data:image/x-icon;base64,%3D
// @description The best torrent search in the world. Aggregated torrents from numerous sites and groups, automated verification, super-fast search.
// @downloadURL https://github.com/Feverqwe/tSearch/raw/master/src/trackers/bitsnoop.js
// @connect *://bitsnoop.com/*
// @require exKit
// @version 1.0.1
// ==/UserScript==

API_exKit({
    id: 'bitsnoop',
    title: 'BitSnoop',
    icon: 'data:image/x-icon;base64,%3D',
    desc: 'The best torrent search in the world. Aggregated torrents from numerous sites and groups, automated verification, super-fast search.',
    flags: {
        auth: 0,
        language: 'en',
        cyrillic: 1
    },
    categoryList: {
        serials: ['tv'],
        music: ['audio'],
        games: ['games', 'game_pc', 'xbox'],
        films: ['video'],
        cartoon: [],
        books: ['books', 'abooks'],
        soft: ['software'],
        anime: [],
        doc: [],
        sport: [],
        xxx: [],
        humor: ['x3']
    },
    search: {
        searchUrl: 'https://bitsnoop.com/search/all/%search%/c/d/1/',
        nextPageSelector: {selector: '#pages a.next', attr: 'href'},
        baseUrl: 'https://bitsnoop.com',
        requestType: 'GET',
        listItemSelector: '#torrents>li',
        torrentSelector: {
            categoryTitle: {selector: '>span.icon', attr: 'title'},
            categoryId: {selector: '>span.icon', attr: 'class'},
            title: 'a',
            url: {selector: 'a', attr: 'href'},
            size: {selector: 'table>tbody>tr>td:eq(0)', childNodeIndex: 0},
            seed: 'div.torInfo>span.seeders',
            peer: 'div.torInfo>span.leechers'
        },
        onGetValue: {
            categoryId: function (details, url) {
                return exKit.funcList.idInCategoryListStr(details.tracker, url, /icon cat_(.+)/);
            },
            size: function (details, value) {
                return exKit.funcList.sizeFormat(value)
            }
        }
    }
});